mapper.select:
SELECT ATTEND_FG as attendFg
FROM TBL_ATTEND
WHERE SCHID = #{schId}
  AND USERID = #{userId}

SELECT A.SCHID as schId, A.GUEST_NM as guestNm, DATE_FORMAT(A.ATT_REG_DT, '%Y-%m-%d %H:%i') as attRegDt, A.USERID as userId, A.GRADE as grade, A.AGE as age, A.GENDER as gender, B.USERNAME as userNm FROM tbl_attend_guest a LEFT OUTER JOIN tbl_users b ON A.USERID = B.USERID
AND A.SCHID = #{schId}
AND A.GUEST_NM = #{guestNm}
AND A.USERID = #{userId}

mapper.delete:
DELETE FROM tbl_attend_guest
WHERE SCHID = #{schId}
  AND GUEST_NM = #{guestNm}
  AND USERID = #{userId}
